mapper.select:
select
id, user_login_id, user_login_pwd, user_name, user_phone, user_role_id, user_system_id
from water_monitor.t_user


        and id = #{id}


        and user_login_id = #{userLoginId}


        and user_login_pwd = #{userLoginPwd}


        and user_name = #{userName}


        and user_phone = #{userPhone}


        and user_role_id = #{userRoleId}


        and user_system_id = #{userSystemId}
